async function applyArmenianToAllTemplates() {
  try {
    console.log('üîÑ Applying Armenian configuration to all templates...');

    // Get all templates
    const allTemplates = await db.select().from(templates);

    console.log(`üìã Found ${allTemplates.length} templates to update`);

    for (const template of allTemplates) {
      console.log(`\nüîß Updating template: ${template.name} (${template.slug})`);

      // Merge Armenian config with existing config
      const existingConfig = (template.config as any) || {};
      const updatedConfig = {
        ...existingConfig,
        ...armenianConfig,
        // Preserve template-specific theme colors if they exist
        theme: {
          ...armenianConfig.theme,
          ...existingConfig?.theme,
          fonts: armenianConfig.theme.fonts // Always use Armenian fonts
        }
      };

      // Update the template
      await db.update(templates)
        .set({ 
          config: updatedConfig,
          updatedAt: new Date()
        })
        .where(eq(templates.id, template.id));

      console.log(`‚úÖ Updated ${template.name} with Armenian content`);
    }

    console.log('\nüéâ All templates updated with Armenian configuration!');
    console.log('\nüìù Armenian content applied:');
    console.log('‚Ä¢ Navigation in Armenian');
    console.log('‚Ä¢ Hero section with Armenian invitation text');
    console.log('‚Ä¢ Timeline events in Armenian');
    console.log('‚Ä¢ RSVP form with Armenian labels');
    console.log('‚Ä¢ Location details in Armenian');
    console.log('‚Ä¢ Calendar with Armenian day labels');
    console.log('‚Ä¢ Armenian-compatible fonts (Noto Sans/Serif Armenian)');
    console.log('‚Ä¢ Countdown timer in Armenian');

  } catch (error) {
    console.error('‚ùå Error applying Armenian configuration:', error);
  }
}

// Run the update
applyArmenianToAllTemplates().then(() => {
  console.log('\nüöÄ Armenian configuration complete!');
  process.exit(0);
}).catch((error) => {
  console.error('üí• Fatal error:', error);
  process.exit(1);
});

