import { db } from '../server/db';
import { templates } from '../shared/schema';
import { nanoid } from 'nanoid';

async function cloneTemplate() {
  try {
    // Get the "Forest & Lily Nature Wedding" template to clone
    const sourceTemplate = await db.query.templates.findFirst({
      where: (templates, { eq }) => eq(templates.slug, 'forest-lily-nature')
    });

    if (!sourceTemplate) {
      console.log('‚ùå Source template not found');
      return;
    }

    console.log(`üìã Found source template: ${sourceTemplate.name}`);

    // Generate a unique slug for the clone
    const timestamp = Date.now();
    const newSlug = `${sourceTemplate.slug}-clone-${timestamp}`;
    const newName = `${sourceTemplate.name} (Clone)`;

    // Create the cloned template
    const clonedTemplate = await db.insert(templates).values({
      id: nanoid(),
      name: newName,
      slug: newSlug,
      templateKey: sourceTemplate.templateKey,
      config: sourceTemplate.config,
      ownerEmail: '',
      maintenance: false,
      maintenancePassword: null,
      sourceTemplateId: sourceTemplate.id,
      isMain: false,
    }).returning();

    console.log(`‚úÖ Successfully cloned template!`);
    console.log(`üìã New template ID: ${clonedTemplate[0].id}`);
    console.log(`üìã New template name: ${clonedTemplate[0].name}`);
    console.log(`üìã New template slug: ${clonedTemplate[0].slug}`);
    console.log(`üåê You can access it at: http://localhost:5173/${clonedTemplate[0].slug}`);
    console.log(`‚öôÔ∏è Edit it at: http://localhost:5173/admin/${clonedTemplate[0].id}`);

    return clonedTemplate[0];
  } catch (error) {
    console.error('‚ùå Error cloning template:', error);
  }
}

// Run the clone function
cloneTemplate().then(() => {
  console.log('üéâ Template cloning completed!');
  process.exit(0);
}).catch((error) => {
  console.error('üí• Fatal error:', error);
  process.exit(1);
});

