import { drizzle } from "drizzle-orm/neon-http";
import { neon } from "@neondatabase/serverless";
import { templates } from "../shared/schema.js";
import { eq } from "drizzle-orm";

const sql = neon(process.env.DATABASE_URL!);
const db = drizzle(sql);

async function fixForestLilyTemplate() {
  try {
    console.log("üîç Checking for Forest & Lily Nature Wedding template...");

    // First, let's see all templates
    const allTemplates = await db.select().from(templates);
    console.log("All templates:", allTemplates.map(t => ({ name: t.name, slug: t.slug, isMain: t.isMain })));

    // Look for the forest-lily template
    const forestLilyTemplate = await db.select()
      .from(templates)
      .where(eq(templates.slug, "forest-lily-nature"));

    if (forestLilyTemplate.length === 0) {
      console.log("‚ùå Forest & Lily template not found in database");
      return;
    }

    const template = forestLilyTemplate[0];
    console.log("Found Forest & Lily template:", {
      name: template.name,
      slug: template.slug,
      isMain: template.isMain,
      maintenance: template.maintenance
    });

    // If it's not marked as main template, fix it
    if (!template.isMain) {
      console.log("üîß Marking Forest & Lily template as main template...");
      await db.update(templates)
        .set({ isMain: true })
        .where(eq(templates.slug, "forest-lily-nature"));
      console.log("‚úÖ Template updated to main template");
    } else {
      console.log("‚úÖ Template is already marked as main");
    }

    // Check if it's in maintenance mode
    if (template.maintenance) {
      console.log("üîß Taking template out of maintenance mode...");
      await db.update(templates)
        .set({ maintenance: false })
        .where(eq(templates.slug, "forest-lily-nature"));
      console.log("‚úÖ Template taken out of maintenance mode");
    }

  } catch (error) {
    console.error("‚ùå Error fixing template:", error);
  }
}

fixForestLilyTemplate();

scripts/migrate-default-template.ts.content:
// Data migration script to populate default template
// Run this after the database migration to populate the default template with existing config

import "dotenv/config";
import { db } from "../server/db";
import { templates } from "../shared/schema";
import { eq } from "drizzle-orm";

// Import the existing wedding config
import { weddingConfig } from "../client/src/config/wedding-config";

export async function migrateDefaultTemplate() {
  try {
    console.log("üîÑ Starting default template migration...");

    // Check if default template already exists
    const existingTemplate = await db
      .select()
      .from(templates)
      .where(eq(templates.id, 'default-harut-tatev'))
      .limit(1);

    if (existingTemplate.length > 0) {
      console.log("‚úÖ Default template already exists, updating config...");

      // Update existing template with current config
      await db
        .update(templates)
        .set({
          config: {
            ...weddingConfig,
            sections: {
              hero: { enabled: true },
              countdown: { enabled: true },
              calendar: { enabled: true },
              locations: { enabled: true },
              timeline: { enabled: true },
              rsvp: { enabled: true },
              photos: { enabled: true },
            },
            theme: {
              colors: {
                primary: "var(--soft-gold)",
                secondary: "var(--sage-green)", 
                accent: "var(--charcoal)",
                background: "var(--cream)",
              },
              fonts: {
                heading: "Playfair Display, serif",
                body: "Inter, sans-serif",
              },
            },
          },
          updatedAt: new Date()
        })
        .where(eq(templates.id, 'default-harut-tatev'));

      console.log("‚úÖ Default template config updated successfully");
    } else {
      console.log("‚ùå Default template not found. Please run the database migration first.");
      return false;
    }

    return true;
  } catch (error) {
    console.error("‚ùå Default template migration failed:", error);
    throw error;
  }
}

// Run migration if called directly
const isMainModule = import.meta.url === `file://${process.argv[1]}`;
if (isMainModule) {
  migrateDefaultTemplate()
    .then(() => {
      console.log("üéâ Migration completed successfully");
      process.exit(0);
    })
    .catch((error) => {
      console.error("üí• Migration failed:", error);
      process.exit(1);
    });
}

