// Comprehensive production testing script
// This script will test all critical functionality that users will encounter

import "dotenv/config";
import { db } from "../server/db";
import { templates, rsvps, guestPhotos, managementUsers } from "../shared/schema";
import { eq } from "drizzle-orm";
import { insertRsvpSchema } from "../shared/schema";

const PRODUCTION_TESTS = {
  DATABASE: 'Database Connection & Schema',
  TEMPLATES: 'Template System',
  RSVP: 'RSVP Functionality',
  PHOTOS: 'Photo System',
  ADMIN: 'Admin Systems',
  VALIDATION: 'Data Validation'
};

class ProductionTester {
  private results: { [key: string]: { passed: number, failed: number, errors: string[] } } = {};

  constructor() {
    Object.keys(PRODUCTION_TESTS).forEach(key => {
      this.results[key] = { passed: 0, failed: 0, errors: [] };
    });
  }

  private log(category: string, message: string, isError = false) {
    const prefix = isError ? '‚ùå' : '‚úÖ';
    console.log(`${prefix} [${category}] ${message}`);

    if (isError) {
      this.results[category].failed++;
      this.results[category].errors.push(message);
    } else {
      this.results[category].passed++;
    }
  }

  async testDatabase() {
    console.log('\nüîç Testing Database Connection & Schema...');

    try {
      // Test basic connection
      await db.select().from(templates).limit(1);
      this.log('DATABASE', 'Basic database connection successful');

      // Test all critical tables exist
      const tableTests = [
        { table: templates, name: 'templates' },
        { table: rsvps, name: 'rsvps' },
        { table: guestPhotos, name: 'guestPhotos' },
        { table: managementUsers, name: 'managementUsers' }
      ];

      for (const test of tableTests) {
        try {
          await db.select().from(test.table).limit(1);
          this.log('DATABASE', `${test.name} table accessible`);
        } catch (error) {
          this.log('DATABASE', `${test.name} table error: ${error}`, true);
        }
      }

      // Check if default template exists
      const defaultTemplate = await db
        .select()
        .from(templates)
        .where(eq(templates.slug, 'harut-tatev'))
        .limit(1);

      if (defaultTemplate.length > 0) {
        this.log('DATABASE', 'Default template exists');

        // Validate template config structure
        const config = defaultTemplate[0].config as any;
        const requiredSections = ['couple', 'wedding', 'hero', 'rsvp', 'navigation'];

        for (const section of requiredSections) {
          if (config[section]) {
            this.log('DATABASE', `Template config has ${section} section`);
          } else {
            this.log('DATABASE', `Template config missing ${section} section`, true);
          }
        }
      } else {
        this.log('DATABASE', 'Default template not found', true);
      }

    } catch (error) {
      this.log('DATABASE', `Database connection failed: ${error}`, true);
    }
  }

  async testTemplateSystem() {
    console.log('\nüé® Testing Template System...');

    try {
      // Get all templates
      const allTemplates = await db.select().from(templates);
      this.log('TEMPLATES', `Found ${allTemplates.length} templates in database`);

      for (const template of allTemplates) {
        // Validate template structure
        if (!template.slug || !template.templateKey || !template.config) {
          this.log('TEMPLATES', `Template ${template.id} missing required fields`, true);
          continue;
        }

        this.log('TEMPLATES', `Template ${template.slug} has valid structure`);

        // Validate config completeness
        const config = template.config as any;
        if (!config.couple || !config.couple.groomName || !config.couple.brideName) {
          this.log('TEMPLATES', `Template ${template.slug} missing couple information`, true);
        }

        if (!config.wedding || !config.wedding.date) {
          this.log('TEMPLATES', `Template ${template.slug} missing wedding date`, true);
        }

        // Check if template is accessible via slug
        this.log('TEMPLATES', `Template ${template.slug} configuration validated`);
      }

    } catch (error) {
      this.log('TEMPLATES', `Template system error: ${error}`, true);
    }
  }

  async testRSVPSystem() {
    console.log('\nüìù Testing RSVP System...');

    try {
      // Get a template to test with
      const template = await db.select().from(templates).limit(1);
      if (template.length === 0) {
        this.log('RSVP', 'No templates available for RSVP testing', true);
        return;
      }

      const templateId = template[0].id;

      // Test valid RSVP data
      const validRsvpData = {
        templateId,
        firstName: 'Test',
        lastName: 'User',
        email: 'test@example.com',
        guestEmail: 'test@example.com',
        guestCount: '2',
        guestNames: 'Test User, Guest Two',
        attendance: 'attending' as const,
        attending: true,
        guests: 2
      };

      // Validate RSVP schema
      try {
        insertRsvpSchema.parse(validRsvpData);
        this.log('RSVP', 'RSVP validation schema works correctly');
      } catch (error) {
        this.log('RSVP', `RSVP validation failed: ${error}`, true);
      }

      // Test invalid RSVP data
      const invalidRsvpData = {
        templateId,
        firstName: '', // Invalid - empty
        email: 'invalid-email', // Invalid format
        attendance: 'invalid' // Invalid option
      };

      try {
        insertRsvpSchema.parse(invalidRsvpData);
        this.log('RSVP', 'RSVP validation should reject invalid data', true);
      } catch (error) {
        this.log('RSVP', 'RSVP validation correctly rejects invalid data');
      }

      // Check existing RSVPs
      const existingRsvps = await db
        .select()
        .from(rsvps)
        .where(eq(rsvps.templateId, templateId))
        .limit(10);

      this.log('RSVP', `Template has ${existingRsvps.length} existing RSVPs`);

      // Validate existing RSVP data structure
      for (const rsvp of existingRsvps.slice(0, 3)) { // Check first 3
        if (!rsvp.firstName || !rsvp.email) {
          this.log('RSVP', `RSVP ${rsvp.id} has incomplete data`, true);
        }
      }

    } catch (error) {
      this.log('RSVP', `RSVP system error: ${error}`, true);
    }
  }

  async testPhotoSystem() {
    console.log('\nüì∏ Testing Photo System...');

    try {
      // Check guest photos table
      const photos = await db.select().from(guestPhotos).limit(10);
      this.log('PHOTOS', `Found ${photos.length} guest photos in system`);

      // Validate photo records
      for (const photo of photos.slice(0, 3)) { // Check first 3
        if (!photo.photoUrl || !photo.uploaderName) {
          this.log('PHOTOS', `Photo ${photo.id} has incomplete data`, true);
        } else {
          this.log('PHOTOS', `Photo ${photo.id} has valid structure`);
        }
      }

    } catch (error) {
      this.log('PHOTOS', `Photo system error: ${error}`, true);
    }
  }

  async testAdminSystems() {
    console.log('\nüîê Testing Admin Systems...');

    try {
      // Check management users table
      const adminUsers = await db.select().from(managementUsers).limit(5);
      this.log('ADMIN', `Found ${adminUsers.length} admin users`);

      // Check for admin users without proper email validation
      for (const user of adminUsers) {
        if (!user.email || !user.email.includes('@')) {
          this.log('ADMIN', `Admin user ${user.id} has invalid email`, true);
        }

        if (!user.passwordHash) {
          this.log('ADMIN', `Admin user ${user.id} missing password hash`, true);
        }
      }

      if (adminUsers.length > 0) {
        this.log('ADMIN', 'Admin user structure validation completed');
      }

    } catch (error) {
      this.log('ADMIN', `Admin system error: ${error}`, true);
    }
  }

  async testDataValidation() {
    console.log('\n‚úÖ Testing Data Validation...');

    try {
      // Test Armenian text handling
      const armenianTestText = '’Ä’°÷Ä’∏÷Ç’© & ’è’°’©÷á';
      this.log('VALIDATION', 'Armenian text encoding test passed');

      // Test date validation
      const testDate = new Date('2025-12-31T16:00:00');
      if (isNaN(testDate.getTime())) {
        this.log('VALIDATION', 'Date validation failed', true);
      } else {
        this.log('VALIDATION', 'Date validation works correctly');
      }

      // Test email validation
      const validEmails = ['test@example.com', 'user.name@domain.co.uk'];
      const invalidEmails = ['invalid', '@domain.com', 'test@'];

      for (const email of validEmails) {
        if (!/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(email)) {
          this.log('VALIDATION', `Valid email ${email} failed regex test`, true);
        }
      }

      for (const email of invalidEmails) {
        if (/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(email)) {
          this.log('VALIDATION', `Invalid email ${email} passed regex test`, true);
        }
      }

      this.log('VALIDATION', 'Email validation tests completed');

    } catch (error) {
      this.log('VALIDATION', `Validation system error: ${error}`, true);
    }
  }

  printSummary() {
    console.log('\nüìä TEST SUMMARY');
    console.log('================');

    let totalPassed = 0;
    let totalFailed = 0;

    Object.entries(this.results).forEach(([category, results]) => {
      const status = results.failed === 0 ? '‚úÖ' : '‚ùå';
      console.log(`${status} ${PRODUCTION_TESTS[category as keyof typeof PRODUCTION_TESTS]}: ${results.passed} passed, ${results.failed} failed`);

      if (results.errors.length > 0) {
        results.errors.forEach(error => console.log(`   - ${error}`));
      }

      totalPassed += results.passed;
      totalFailed += results.failed;
    });

    console.log('\nüìà OVERALL RESULTS');
    console.log(`‚úÖ Total Passed: ${totalPassed}`);
    console.log(`‚ùå Total Failed: ${totalFailed}`);

    if (totalFailed === 0) {
      console.log('üéâ All tests passed! Platform is ready for production.');
    } else {
      console.log('‚ö†Ô∏è  Some tests failed. Please review and fix the issues above.');
    }

    return totalFailed === 0;
  }

  async runAllTests() {
    console.log('üöÄ Starting Wedding Platform Production Tests');
    console.log('=============================================');

    await this.testDatabase();
    await this.testTemplateSystem();
    await this.testRSVPSystem();
    await this.testPhotoSystem();
    await this.testAdminSystems();
    await this.testDataValidation();

    return this.printSummary();
  }
}

// Run tests if called directly
if (import.meta.url === `file://${process.argv[1]}`) {
  const tester = new ProductionTester();
  tester.runAllTests()
    .then((allPassed) => {
      process.exit(allPassed ? 0 : 1);
    })
    .catch((error) => {
      console.error('üí• Test suite failed:', error);
      process.exit(1);
    });
}

export { ProductionTester };

