      // Verify templates page loads
      await expect(page.locator('h1')).toContainText(/Templates|Choose/);

      // Check if templates are displayed
      const templates = page.locator('[data-testid="template-card"], .template-card');
      await expect(templates.first()).toBeVisible({ timeout: 10000 });
