  test.describe('RSVP Functionality', () => {
    test('should submit RSVP successfully', async ({ page }) => {
      await page.goto('/harut-tatev-2025');

      // Navigate to RSVP section
      await page.click('text=/RSVP|’Ä’°’Ω’ø’°’ø’∏÷Ç’¥/');

      // Wait for RSVP form to be visible
      await expect(page.locator('form')).toBeVisible({ timeout: 10000 });

      // Fill out RSVP form
