      await expect(successMessage).toBeVisible({ timeout: 10000 });
    });

    test('should validate required RSVP fields', async ({ page }) => {
      await page.goto('/harut-tatev-2025');

      // Navigate to RSVP section
      await page.click('text=/RSVP|’Ä’°’Ω’ø’°’ø’∏÷Ç’¥/');

      // Wait for form
      await expect(page.locator('form')).toBeVisible();

      // Try to submit without filling required fields
      await page.click('button[type="submit"]');

      // Check for validation errors
      const errorMessages = page.locator('.error, [role="alert"], .text-red');
      await expect(errorMessages.first()).toBeVisible({ timeout: 5000 });
    });
  });

  test.describe('Photo Gallery', () => {
    test('should display photo gallery', async ({ page }) => {
      await page.goto('/harut-tatev-2025');

      // Navigate to photos section
      await page.click('text=/Photos|‘º’∏÷Ç’Ω’°’∂’Ø’°÷Ä’∂’•÷Ä/');

      // Check if photo gallery loads
      const photoSection = page.locator('.photos, [data-section="photos"], .gallery');
      await expect(photoSection).toBeVisible({ timeout: 10000 });
    });

    test('should handle photo upload (if enabled)', async ({ page }) => {
      await page.goto('/harut-tatev-2025');

      // Navigate to photos
      await page.click('text=/Photos|‘º’∏÷Ç’Ω’°’∂’Ø’°÷Ä’∂’•÷Ä/');

      // Look for upload button
      const uploadButton = page.locator('button:has-text("Upload"), button:has-text("’é’•÷Ä’¢’•’º’∂’•’¨")');

      if (await uploadButton.count() > 0) {
        await uploadButton.click();

        // Check if upload interface appears
        const uploadModal = page.locator('.modal, .dialog, [role="dialog"]');
        await expect(uploadModal).toBeVisible({ timeout: 5000 });
      }
    });
  });

  test.describe('Admin Panel Access', () => {
    test('should access admin login page', async ({ page }) => {
      await page.goto('/admin/login');

      // Check login form is displayed
      await expect(page.locator('form')).toBeVisible();
      await expect(page.locator('input[type="email"], input[name="email"]')).toBeVisible();
      await expect(page.locator('input[type="password"], input[name="password"]')).toBeVisible();
    });

    test('should handle invalid admin credentials', async ({ page }) => {
      await page.goto('/admin/login');

      // Fill invalid credentials
      await page.fill('input[type="email"], input[name="email"]', 'invalid@test.com');
      await page.fill('input[type="password"], input[name="password"]', 'wrongpassword');

      // Submit form
      await page.click('button[type="submit"]');

      // Check for error message
      const errorMessage = page.locator('.error, [role="alert"], .text-red');
      await expect(errorMessage).toBeVisible({ timeout: 5000 });
    });
  });

  test.describe('Platform Admin', () => {
    test('should access platform admin login', async ({ page }) => {
      await page.goto('/platform-admin/login');

      // Check platform admin login form
      await expect(page.locator('form')).toBeVisible();
      await expect(page.locator('input[name="username"], input[type="text"]')).toBeVisible();
      await expect(page.locator('input[type="password"]')).toBeVisible();
    });
  });

  test.describe('Mobile Responsiveness', () => {
    test('should work properly on mobile devices', async ({ page }) => {
      // Set mobile viewport
      await page.setViewportSize({ width: 375, height: 667 });

      await page.goto('/harut-tatev-2025');

      // Check mobile navigation
      const mobileNav = page.locator('.mobile-nav, [data-mobile-nav], .hamburger');
      if (await mobileNav.count() > 0) {
        await expect(mobileNav).toBeVisible();
      }

      // Check content is readable on mobile
      const content = page.locator('main, .content');
      await expect(content).toBeVisible();

      // Test RSVP form on mobile
      await page.click('text=/RSVP|’Ä’°’Ω’ø’°’ø’∏÷Ç’¥/');
      const form = page.locator('form');
      await expect(form).toBeVisible();
    });
  });

  test.describe('Performance and Loading', () => {
    test('should load pages within reasonable time', async ({ page }) => {
      const startTime = Date.now();

      await page.goto('/harut-tatev-2025');
      await page.waitForLoadState('networkidle');

      const loadTime = Date.now() - startTime;

      // Should load within 10 seconds
      expect(loadTime).toBeLessThan(10000);
    });

    test('should not have console errors', async ({ page }) => {
      const consoleErrors: string[] = [];

      page.on('console', (message) => {
        if (message.type() === 'error') {
          consoleErrors.push(message.text());
        }
      });

      await page.goto('/harut-tatev-2025');
      await page.waitForLoadState('networkidle');

      // Filter out known acceptable errors (like network errors in dev)
      const criticalErrors = consoleErrors.filter(error => 
        !error.includes('net::') && 
        !error.includes('favicon') &&
        !error.includes('WebSocket')
      );

      expect(criticalErrors).toHaveLength(0);
    });
  });
});

