// API Integration Tests
import { describe, it, expect, beforeAll, afterAll } from 'vitest';
import request from 'supertest';
import { app } from '../../server/index';
import { testUtils } from '../setup';

describe('Wedding Platform API Tests', () => {
  let testTemplateId: string;

  beforeAll(async () => {
    // Create test template
    const template = await testUtils.createTestTemplate();
    testTemplateId = template.id;
  });

  afterAll(async () => {
    // Cleanup
    if (testTemplateId) {
      await testUtils.cleanupTestTemplate(testTemplateId);
    }
  });

  describe('Health and Basic Routes', () => {
    it('should return health status', async () => {
      const response = await request(app)
        .get('/health')
        .expect(200);

      expect(response.body).toHaveProperty('status', 'ok');
      expect(response.body).toHaveProperty('timestamp');
    });

    it('should return API test status', async () => {
      const response = await request(app)
        .get('/api/test')
        .expect(200);

      expect(response.body).toHaveProperty('message', 'Server is running');
      expect(response.body).toHaveProperty('hasDatabase', true);
    });
  });

  describe('Template System', () => {
    it('should fetch template configuration', async () => {
      const response = await request(app)
        .get(`/api/templates/${testTemplateId}/config`)
        .expect(200);

      expect(response.body).toHaveProperty('config');
      expect(response.body.config).toHaveProperty('couple');
      expect(response.body.config).toHaveProperty('wedding');
    });

    it('should handle non-existent template', async () => {
      await request(app)
        .get('/api/templates/non-existent-id/config')
        .expect(404);
    });
  });

  describe('RSVP System', () => {
    it('should submit valid RSVP', async () => {
      const rsvpData = testUtils.createTestRSVP(testTemplateId);

      const response = await request(app)
        .post(`/api/templates/${testTemplateId}/rsvps`)
        .send(rsvpData)
        .expect(200);

      expect(response.body).toHaveProperty('id');
      expect(response.body).toHaveProperty('firstName', 'John');
    });

    it('should reject invalid RSVP data', async () => {
      const invalidRsvp = {
        templateId: testTemplateId,
        // Missing required fields
      };

      await request(app)
        .post(`/api/templates/${testTemplateId}/rsvps`)
        .send(invalidRsvp)
        .expect(400);
    });

    it('should fetch RSVP list for template', async () => {
      const response = await request(app)
        .get(`/api/admin/templates/${testTemplateId}/rsvps`)
        .expect(200);

      expect(Array.isArray(response.body)).toBe(true);
    });
  });
});

