// Enhanced Image Display Component for Admin Panel
// Handles missing images gracefully with fallbacks and loading states

import React, { useState, useCallback } from 'react';
import { X, Upload, AlertCircle, Image as ImageIcon } from 'lucide-react';
import { Button } from '@/components/ui/button';
import { Alert, AlertDescription } from '@/components/ui/alert';
import { Card, CardContent } from '@/components/ui/card';
import { cn } from '@/lib/utils';

interface SafeImageProps {
  src?: string;
  alt: string;
  fallbackSrc?: string;
  className?: string;
  onError?: () => void;
  onLoad?: () => void;
  showErrorMessage?: boolean;
}

export const SafeImage: React.FC<SafeImageProps> = ({
  src,
  alt,
  fallbackSrc = '/attached_assets/default-wedding-couple.jpg',
  className = '',
  onError,
  onLoad,
  showErrorMessage = false
}) => {
  const [imgSrc, setImgSrc] = useState(src);
  const [hasError, setHasError] = useState(false);
  const [isLoading, setIsLoading] = useState(true);
  const [retryCount, setRetryCount] = useState(0);

  const handleError = useCallback(() => {
    console.warn(`Image failed to load: ${imgSrc}`);
    setHasError(true);
    setIsLoading(false);

    if (retryCount === 0 && imgSrc !== fallbackSrc) {
      // Try fallback image once
      setImgSrc(fallbackSrc);
      setRetryCount(1);
      setHasError(false);
      setIsLoading(true);
    } else {
      onError?.();
    }
  }, [imgSrc, fallbackSrc, retryCount, onError]);

  const handleLoad = useCallback(() => {
    setIsLoading(false);
    setHasError(false);
    onLoad?.();
  }, [onLoad]);

  // Reset state when src changes
  React.useEffect(() => {
    if (src !== imgSrc && retryCount === 0) {
      setImgSrc(src);
      setHasError(false);
      setIsLoading(true);
      setRetryCount(0);
    }
  }, [src]);

  if (hasError && retryCount > 0) {
    return (
      <div className={cn(
        "flex flex-col items-center justify-center bg-gray-50 border-2 border-dashed border-gray-200 rounded-lg p-4",
        className
      )}>
        <ImageIcon className="w-8 h-8 text-gray-400 mb-2" />
        <span className="text-sm text-gray-500 text-center">
          {showErrorMessage ? `Failed to load: ${alt}` : alt}
        </span>
        {showErrorMessage && (
          <Button 
            variant="ghost" 
            size="sm" 
            onClick={() => {
              setImgSrc(src);
              setHasError(false);
              setIsLoading(true);
              setRetryCount(0);
            }}
            className="mt-2"
          >
            Retry
          </Button>
        )}
      </div>
    );
  }

  return (
    <div className={cn("relative", className)}>
      {isLoading && (
        <div className="absolute inset-0 flex items-center justify-center bg-gray-50 rounded-lg">
          <div className="animate-spin rounded-full h-6 w-6 border-b-2 border-blue-500"></div>
        </div>
      )}
      <img
        src={imgSrc}
        alt={alt}
        onLoad={handleLoad}
        onError={handleError}
        className={cn(
          "w-full h-full object-cover rounded-lg transition-opacity duration-200",
          isLoading ? "opacity-0" : "opacity-100"
        )}
        loading="lazy"
      />
    </div>
  );
};

interface ImageGalleryProps {
  images: string[];
  onImageRemove?: (index: number) => void;
  onImageAdd?: () => void;
  title?: string;
  maxImages?: number;
  editable?: boolean;
  className?: string;
}

export const ImageGallery: React.FC<ImageGalleryProps> = ({
  images = [],
  onImageRemove,
  onImageAdd,
  title = "Images",
  maxImages = 10,
  editable = false,
  className = ""
}) => {
  const [loadingStates, setLoadingStates] = useState<Record<number, boolean>>({});
  const [errorStates, setErrorStates] = useState<Record<number, boolean>>({});

  const handleImageLoad = (index: number) => {
    setLoadingStates(prev => ({ ...prev, [index]: false }));
    setErrorStates(prev => ({ ...prev, [index]: false }));
  };

  const handleImageError = (index: number) => {
    setLoadingStates(prev => ({ ...prev, [index]: false }));
    setErrorStates(prev => ({ ...prev, [index]: true }));
  };

  const canAddImages = editable && images.length < maxImages;

  return (
    <Card className={className}>
      <CardContent className="p-6">
        <div className="flex items-center justify-between mb-4">
          <h3 className="text-lg font-semibold">{title}</h3>
          <span className="text-sm text-gray-500">
            {images.length} / {maxImages} images
          </span>
        </div>

        {images.length === 0 ? (
          <div className="text-center py-8">
            <ImageIcon className="w-12 h-12 text-gray-300 mx-auto mb-4" />
            <p className="text-gray-500 mb-4">No images uploaded yet</p>
            {canAddImages && (
              <Button onClick={onImageAdd} variant="outline">
                <Upload className="w-4 h-4 mr-2" />
                Add Images
              </Button>
            )}
          </div>
        ) : (
          <>
            <div className="grid grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4">
              {images.map((image, index) => (
                <div key={index} className="relative group">
                  <div className="aspect-square">
                    <SafeImage
                      src={image}
                      alt={`Image ${index + 1}`}
                      className="w-full h-full"
                      onLoad={() => handleImageLoad(index)}
                      onError={() => handleImageError(index)}
                      showErrorMessage={true}
                    />
                  </div>

                  {editable && onImageRemove && (
                    <Button
                      variant="destructive"
                      size="sm"
                      className="absolute top-2 right-2 h-6 w-6 p-0 opacity-0 group-hover:opacity-100 transition-opacity"
                      onClick={() => onImageRemove(index)}
                    >
                      <X className="h-3 w-3" />
                    </Button>
                  )}

                  {errorStates[index] && (
                    <div className="absolute bottom-2 left-2 right-2">
                      <Alert variant="destructive" className="py-1 px-2">
                        <AlertCircle className="h-3 w-3" />
                        <AlertDescription className="text-xs">
                          Failed to load
                        </AlertDescription>
                      </Alert>
                    </div>
                  )}
                </div>
              ))}

              {canAddImages && (
                <button
                  onClick={onImageAdd}
                  className="aspect-square flex flex-col items-center justify-center border-2 border-dashed border-gray-300 rounded-lg hover:border-gray-400 transition-colors"
                >
                  <Upload className="w-6 h-6 text-gray-400 mb-2" />
                  <span className="text-sm text-gray-500">Add Image</span>
                </button>
              )}
            </div>

            {images.length > 0 && canAddImages && (
              <div className="mt-4 text-center">
                <Button onClick={onImageAdd} variant="outline" size="sm">
                  <Upload className="w-4 h-4 mr-2" />
                  Add More Images
                </Button>
              </div>
            )}
          </>
        )}
      </CardContent>
    </Card>
  );
};

export default ImageGallery;

client/src/components/ui/section-image-u.content:
import React, { useState, useRef, useCallback } from 'react';
import { Upload, X, Eye, ChevronLeft, ChevronRight } from 'lucide-react';
import { Button } from './button';
import { Card, CardContent } from './card';
import { cn } from '@/lib/utils';
import { ImageUploader } from './image-uploader';
import { SafeImage } from './safe-image';

interface SectionImageUploaderProps {
  templateId: string;
  sectionType: 'hero' | 'gallery' | 'background';
  sectionTitle: string;
  existingImages?: string[];
  onImagesUpdate?: (images: string[]) => void;
  maxImages?: number;
  className?: string;
  disabled?: boolean;
}

export default function SectionImageUploader({
  templateId,
  sectionType,
  sectionTitle,
  existingImages = [],
  onImagesUpdate,
  maxImages = 10,
  className,
  disabled = false,
}: SectionImageUploaderProps) {
  const [previewMode, setPreviewMode] = useState(false);
  const [currentImageIndex, setCurrentImageIndex] = useState(0);

  const handleImagesUploaded = useCallback((newUrls: string[]) => {
    const updatedImages = [...existingImages, ...newUrls];
    onImagesUpdate?.(updatedImages);
  }, [existingImages, onImagesUpdate]);

  const handleImageRemoved = useCallback((removedUrl: string) => {
    const updatedImages = existingImages.filter(url => url !== removedUrl);
    onImagesUpdate?.(updatedImages);
  }, [existingImages, onImagesUpdate]);

  const nextImage = useCallback(() => {
    setCurrentImageIndex(prev => 
      prev + 1 >= existingImages.length ? 0 : prev + 1
    );
  }, [existingImages.length]);

  const prevImage = useCallback(() => {
    setCurrentImageIndex(prev => 
      prev - 1 < 0 ? existingImages.length - 1 : prev - 1
    );
  }, [existingImages.length]);

  const ImagePreview = () => {
    if (existingImages.length === 0) {
      return (
        <div className="text-center py-8">
          <p className="text-muted-foreground">No images uploaded yet</p>
        </div>
      );
    }

    if (existingImages.length === 1) {
      return (
        <div className="relative">
          <SafeImage
            src={existingImages[0]} 
            alt={`${sectionTitle} image`}
            className="w-full h-64 object-cover rounded-lg"
            showErrorMessage={true}
          />
        </div>
      );
    }

    return (
      <div className="relative">
        <SafeImage
          src={existingImages[currentImageIndex]} 
          alt={`${sectionTitle} image ${currentImageIndex + 1}`}
          className="w-full h-64 object-cover rounded-lg"
          showErrorMessage={true}
        />

        {/* Navigation */}
        <Button
          variant="outline"
          size="sm"
          className="absolute left-2 top-1/2 transform -translate-y-1/2 w-8 h-8 p-0"
          onClick={prevImage}
        >
          <ChevronLeft className="w-4 h-4" />
        </Button>

        <Button
          variant="outline"
          size="sm"
          className="absolute right-2 top-1/2 transform -translate-y-1/2 w-8 h-8 p-0"
          onClick={nextImage}
        >
          <ChevronRight className="w-4 h-4" />
        </Button>

        {/* Dots indicator */}
        <div className="absolute bottom-2 left-1/2 transform -translate-x-1/2 flex gap-1">
          {existingImages.map((_, index) => (
            <button
              key={index}
              className={cn(
                "w-2 h-2 rounded-full transition-colors",
                index === currentImageIndex ? "bg-white" : "bg-white/50"
              )}
              onClick={() => setCurrentImageIndex(index)}
            />
          ))}
        </div>
      </div>
    );
  };

  if (previewMode) {
    return (
      <Card className="overflow-hidden">
        <CardContent className="p-6">
          <div className="flex items-center justify-between mb-4">
            <h3 className="text-lg font-semibold">{sectionTitle} Preview</h3>
            <Button
              variant="outline"
              size="sm"
              onClick={() => setPreviewMode(false)}
            >
              <ChevronLeft className="w-4 h-4 mr-2" />
              Back to Editor
            </Button>
          </div>
          <ImagePreview />
          <p className="text-sm text-muted-foreground mt-2 text-center">
            {existingImages.length} image{existingImages.length !== 1 ? 's' : ''} in {sectionTitle.toLowerCase()}
          </p>
        </CardContent>
      </Card>
    );
  }

  return (
    <Card className={cn("overflow-hidden", className)}>
      <CardContent className="p-6">
        <div className="flex items-center justify-between mb-4">
          <h3 className="text-lg font-semibold">{sectionTitle} Images</h3>
          {existingImages.length > 0 && (
            <Button
              variant="outline"
              size="sm"
              onClick={() => setPreviewMode(true)}
            >
              <Eye className="w-4 h-4 mr-2" />
              Preview
            </Button>
          )}
        </div>

        <ImageUploader
          templateId={templateId}
          category={sectionType}
          maxFiles={maxImages}
          onImagesUploaded={handleImagesUploaded}
          onImageRemoved={handleImageRemoved}
          disabled={disabled}
          existingImages={existingImages}
        />
      </CardContent>
    </Card>
  );
}

client/src/components/ui/section-manager.content:
import React, { useState } from 'react';
import { Button } from '@/components/ui/button';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Badge } from '@/components/ui/badge';
import { 
  ChevronUp, 
  ChevronDown, 
  Eye, 
  EyeOff, 
  Plus, 
  Trash2, 
  GripVertical,
  Home,
  Clock,
  Calendar,
  MapPin,
  Clock4,
  Users,
  Camera
} from 'lucide-react';
import { cn } from '@/lib/utils';
import type { WeddingConfig } from '@/templates/types';

interface SectionConfig {
  id: string;
  name: string;
  title: string;
  icon: React.ComponentType<any>;
  enabled: boolean;
  order: number;
  required?: boolean; // Some sections might be required
}

interface SectionManagerProps {
  config: WeddingConfig;
  onConfigChange: (newConfig: WeddingConfig) => void;
  className?: string;
}

const DEFAULT_SECTIONS: Omit<SectionConfig, 'enabled' | 'order'>[] = [
  { id: 'hero', name: 'Hero Section', title: 'Welcome & Hero', icon: Home, required: true },
  { id: 'countdown', name: 'Countdown Timer', title: 'Wedding Countdown', icon: Clock },
  { id: 'calendar', name: 'Calendar', title: 'Wedding Date', icon: Calendar },
  { id: 'locations', name: 'Locations', title: 'Venue Information', icon: MapPin },
  { id: 'timeline', name: 'Timeline', title: 'Wedding Timeline', icon: Clock4 },
  { id: 'rsvp', name: 'RSVP Form', title: 'Guest Registration', icon: Users },
  { id: 'photos', name: 'Photo Gallery', title: 'Wedding Photos', icon: Camera },
];

export function SectionManager({ config, onConfigChange, className }: SectionManagerProps) {
  const [sections, setSections] = useState<SectionConfig[]>(() => {
    // Initialize sections from config
    const currentSections = config.sections || {};

    return DEFAULT_SECTIONS.map((section, index) => ({
      ...section,
      enabled: currentSections[section.id as keyof typeof currentSections]?.enabled !== false,
      order: currentSections[section.id as keyof typeof currentSections]?.order ?? index,
    })).sort((a, b) => a.order - b.order);
  });

  const updateConfig = (newSections: SectionConfig[]) => {
    const sectionsConfig: WeddingConfig['sections'] = {};

    newSections.forEach(section => {
      (sectionsConfig as any)[section.id] = { 
        enabled: section.enabled,
        order: section.order 
      };
    });

    const newConfig: WeddingConfig = {
      ...config,
      sections: sectionsConfig,
    };

    onConfigChange(newConfig);
  };

  const moveSection = (fromIndex: number, toIndex: number) => {
    if (fromIndex === toIndex) return;

    const newSections = [...sections];
    const [movedSection] = newSections.splice(fromIndex, 1);
    newSections.splice(toIndex, 0, movedSection);

    // Update order values
    const reorderedSections = newSections.map((section, index) => ({
      ...section,
      order: index,
    }));

    setSections(reorderedSections);
    updateConfig(reorderedSections);
  };

  const toggleSection = (sectionId: string) => {
    const newSections = sections.map(section =>
      section.id === sectionId
        ? { ...section, enabled: !section.enabled }
        : section
    );

    setSections(newSections);
    updateConfig(newSections);
  };

  const addSection = (sectionId: string) => {
    const sectionTemplate = DEFAULT_SECTIONS.find(s => s.id === sectionId);
    if (!sectionTemplate || sections.find(s => s.id === sectionId)) return;

    const newSection: SectionConfig = {
      ...sectionTemplate,
      enabled: true,
      order: sections.length,
    };

    const newSections = [...sections, newSection];
    setSections(newSections);
    updateConfig(newSections);
  };

  const removeSection = (sectionId: string) => {
    const section = sections.find(s => s.id === sectionId);
    if (!section || section.required) return;

    const newSections = sections
      .filter(s => s.id !== sectionId)
      .map((section, index) => ({ ...section, order: index }));

    setSections(newSections);
    updateConfig(newSections);
  };

  const availableSections = DEFAULT_SECTIONS.filter(
    defaultSection => !sections.find(section => section.id === defaultSection.id)
  );

  return (
    <Card className={cn('w-full', className)}>
      <CardHeader>
        <CardTitle className="flex items-center gap-2">
          <GripVertical className="w-5 h-5" />
          Section Manager
        </CardTitle>
        <p className="text-sm text-muted-foreground">
          Manage the sections that appear on your wedding website. Drag to reorder, toggle visibility, or add/remove sections.
        </p>
      </CardHeader>

      <CardContent className="space-y-4">
        {/* Current Sections */}
        <div className="space-y-2">
          <h4 className="font-medium text-sm">Current Sections</h4>

          {sections.map((section, index) => {
            const IconComponent = section.icon;

            return (
              <div
                key={section.id}
                className={cn(
                  'flex items-center gap-3 p-3 rounded-lg border transition-colors',
                  section.enabled ? 'bg-background' : 'bg-muted/50'
                )}
              >
                {/* Drag Handle */}
                <div className="cursor-move text-muted-foreground">
                  <GripVertical className="w-4 h-4" />
                </div>

                {/* Section Icon */}
                <div className={cn(
                  'w-8 h-8 rounded-md flex items-center justify-center',
                  section.enabled ? 'bg-primary/10 text-primary' : 'bg-muted text-muted-foreground'
                )}>
                  <IconComponent className="w-4 h-4" />
                </div>

                {/* Section Info */}
                <div className="flex-1 min-w-0">
                  <h5 className={cn(
                    'font-medium text-sm truncate',
                    !section.enabled && 'text-muted-foreground'
                  )}>
                    {section.title}
                  </h5>
                  <p className="text-xs text-muted-foreground truncate">
                    {section.name}
                  </p>
                </div>

                {/* Status Badge */}
                <Badge 
                  variant={section.enabled ? 'default' : 'secondary'}
                  className="text-xs"
                >
                  {section.enabled ? 'Visible' : 'Hidden'}
                </Badge>

                {/* Actions */}
                <div className="flex items-center gap-1">
                  {/* Move Up */}
                  <Button
                    size="sm"
                    variant="ghost"
                    className="w-8 h-8 p-0"
                    onClick={() => moveSection(index, index - 1)}
                    disabled={index === 0}
                  >
                    <ChevronUp className="w-4 h-4" />
                  </Button>

                  {/* Move Down */}
                  <Button
                    size="sm"
                    variant="ghost"
                    className="w-8 h-8 p-0"
                    onClick={() => moveSection(index, index + 1)}
                    disabled={index === sections.length - 1}
                  >
                    <ChevronDown className="w-4 h-4" />
                  </Button>

                  {/* Toggle Visibility */}
                  <Button
                    size="sm"
                    variant="ghost"
                    className="w-8 h-8 p-0"
                    onClick={() => toggleSection(section.id)}
                  >
                    {section.enabled ? (
                      <Eye className="w-4 h-4" />
                    ) : (
                      <EyeOff className="w-4 h-4" />
                    )}
                  </Button>

                  {/* Remove Section */}
                  {!section.required && (
                    <Button
                      size="sm"
                      variant="ghost"
                      className="w-8 h-8 p-0 text-destructive hover:text-destructive"
                      onClick={() => removeSection(section.id)}
                    >
                      <Trash2 className="w-4 h-4" />
                    </Button>
                  )}
                </div>
              </div>
            );
          })}

          {sections.length === 0 && (
            <div className="text-center py-8 text-muted-foreground">
              <p className="text-sm">No sections configured</p>
            </div>
          )}
        </div>

        {/* Add Section */}
        {availableSections.length > 0 && (
          <div className="space-y-2 pt-4 border-t">
            <h4 className="font-medium text-sm">Add Sections</h4>

            <div className="grid grid-cols-1 gap-2">
              {availableSections.map((section) => {
                const IconComponent = section.icon;

                return (
                  <Button
                    key={section.id}
                    variant="outline"
                    className="justify-start gap-3 h-auto py-3"
                    onClick={() => addSection(section.id)}
                  >
                    <div className="w-8 h-8 rounded-md bg-primary/10 text-primary flex items-center justify-center">
                      <IconComponent className="w-4 h-4" />
                    </div>

                    <div className="text-left">
                      <p className="font-medium text-sm">{section.title}</p>
                      <p className="text-xs text-muted-foreground">{section.name}</p>
                    </div>

                    <Plus className="w-4 h-4 ml-auto" />
                  </Button>
                );
              })}
            </div>
          </div>
        )}

        {/* Summary */}
        <div className="pt-4 border-t">
          <div className="flex items-center justify-between text-sm">
            <span className="text-muted-foreground">
              {sections.filter(s => s.enabled).length} of {sections.length} sections visible
            </span>

            <Button
              size="sm"
              variant="outline"
              onClick={() => {
                // Reset to default configuration
                const defaultSections = DEFAULT_SECTIONS.map((section, index) => ({
                  ...section,
                  enabled: true,
                  order: index,
                }));
                setSections(defaultSections);
                updateConfig(defaultSections);
              }}
            >
              Reset to Default
            </Button>
          </div>
        </div>
      </CardContent>
    </Card>
  );
}

