import { useState, useEffect } from "react";
import { Switch, Route, useLocation } from "wouter";
import { queryClient } from "./lib/queryClient";
import { QueryClientProvider } from "@tanstack/react-query";
import { Toaster } from "@/components/ui/toaster";
import { TooltipProvider } from "@/components/ui/tooltip";
import { MaintenanceMode } from "@/components/maintenance-mode";
import { AdminPanel } from "@/components/admin-panel";
import { weddingConfig } from "@/config/wedding-config";
import Home from "@/pages/home";
import PhotosPage from "@/pages/photos";
import NotFound from "@/pages/not-found";
import LoadingScreen from "@/components/loading-screen";

function Router() {
  return (
    <Switch>
      <Route path="/" component={Home} />
      <Route path="/photos" component={PhotosPage} />
      <Route path="/admin" component={AdminPanel} />
      <Route component={NotFound} />
    </Switch>
  );
}

function App() {
  const [location] = useLocation();
  const [maintenanceBypassed, setMaintenanceBypassed] = useState(false);
  const [maintenanceEnabled, setMaintenanceEnabled] = useState(false);
  const [loading, setLoading] = useState(true);

  // Check maintenance status from server and bypass conditions
  useEffect(() => {
    const checkMaintenanceStatus = async (retryCount = 0) => {
      try {
        const response = await fetch("/api/maintenance");
        if (!response.ok) throw new Error(`HTTP ${response.status}`);

        const data = await response.json();
        setMaintenanceEnabled(data.enabled);
        console.log("üîß Maintenance status loaded:", data.enabled);

        // Check bypass conditions
        const bypassKey = localStorage.getItem("maintenance-bypass");
        const urlParams = new URLSearchParams(window.location.search);
        const previewParam = urlParams.get("preview");

        // Allow bypass with URL parameter or localStorage
        if (bypassKey === "true" || previewParam === "true") {
          setMaintenanceBypassed(true);
        }
      } catch (error) {
        console.error("Failed to check maintenance status:", error);

        // Retry once after 2 seconds if first attempt fails
        if (retryCount === 0) {
          console.log("üîÑ Retrying maintenance status check...");
          setTimeout(() => checkMaintenanceStatus(1), 2000);
          return;
        }

        // Don't change maintenance state on API failure to prevent automatic turn-off
        console.warn("‚ö†Ô∏è Keeping current maintenance state due to API failure");
      } finally {
        if (retryCount > 0) setLoading(false);
      }
    };

    checkMaintenanceStatus();
    setTimeout(() => setLoading(false), 5000); // Failsafe timeout
  }, []);

  const handlePasswordCorrect = () => {
    setMaintenanceBypassed(true);
    localStorage.setItem("maintenance-bypass", "true");
  };

  // Show maintenance mode if enabled and not bypassed, but allow admin panel access
  const isAdminRoute = location === "/admin";
  const shouldShowMaintenance = maintenanceEnabled && !maintenanceBypassed && !isAdminRoute;

  // Show loading state while checking maintenance status
  if (loading) {
    return <LoadingScreen />;
  }

  if (shouldShowMaintenance) {
    return (
      <QueryClientProvider client={queryClient}>
        <TooltipProvider>
          <Toaster />
          <MaintenanceMode onPasswordCorrect={handlePasswordCorrect} />
        </TooltipProvider>
      </QueryClientProvider>
    );
  }

  return (
    <QueryClientProvider client={queryClient}>
      <TooltipProvider>
        <Toaster />
        <Router />
      </TooltipProvider>
    </QueryClientProvider>
  );
}

export default App;

client/src/index.css.content:
@import url('https://fonts.googleapis.com/css2?family=Playfair+Display:wght@400;600;700&family=Inter:wght@300;400;500;600&display=swap');

@tailwind base;
@tailwind components;
@tailwind utilities;

:root {
  --background: hsl(340, 30%, 97%);
  --foreground: hsl(340, 15%, 15%);
  --card: hsl(0, 0%, 100%);
  --card-foreground: hsl(340, 15%, 15%);
  --popover: hsl(0, 0%, 100%);
  --popover-foreground: hsl(340, 15%, 15%);
  --primary: hsl(340, 45%, 65%);
  --primary-foreground: hsl(0, 0%, 100%);
  --secondary: hsl(340, 20%, 90%);
  --secondary-foreground: hsl(340, 15%, 15%);
  --muted: hsl(340, 15%, 92%);
  --muted-foreground: hsl(340, 10%, 50%);
  --accent: hsl(340, 25%, 88%);
  --accent-foreground: hsl(340, 15%, 15%);
  --destructive: hsl(0, 84%, 60%);
  --destructive-foreground: hsl(0, 0%, 100%);
  --border: hsl(340, 15%, 85%);
  --input: hsl(340, 20%, 96%);
  --ring: hsl(340, 45%, 65%);

  /* Custom wedding colors - Blush Pink Palette */
  --cream: hsl(340, 30%, 97%);
  --soft-gold: hsl(340, 45%, 65%);
  --sage-green: hsl(340, 20%, 80%);
  --warm-beige: hsl(340, 25%, 88%);
  --charcoal: hsl(340, 15%, 15%);
  --light-gold: hsl(340, 30%, 85%);

  --font-serif: 'Playfair Display', serif;
  --font-sans: 'Inter', sans-serif;
  --radius: 0.75rem;
}

.dark {
  --background: hsl(0, 0%, 5%);
  --foreground: hsl(0, 0%, 90%);
  --card: hsl(0, 0%, 8%);
  --card-foreground: hsl(0, 0%, 90%);
  --popover: hsl(0, 0%, 8%);
  --popover-foreground: hsl(0, 0%, 90%);
  --primary: hsl(45, 80%, 50%);
  --primary-foreground: hsl(0, 0%, 100%);
  --secondary: hsl(0, 0%, 15%);
  --secondary-foreground: hsl(0, 0%, 90%);
  --muted: hsl(0, 0%, 12%);
  --muted-foreground: hsl(0, 0%, 60%);
  --accent: hsl(0, 0%, 12%);
  --accent-foreground: hsl(0, 0%, 90%);
  --destructive: hsl(0, 84%, 60%);
  --destructive-foreground: hsl(0, 0%, 100%);
  --border: hsl(0, 0%, 15%);
  --input: hsl(0, 0%, 12%);
  --ring: hsl(45, 80%, 50%);
}

@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply bg-background text-foreground font-sans antialiased;
    background-color: var(--cream);
    color: var(--charcoal);
  }

  html {
    scroll-behavior: smooth;
  }
}

@layer components {
  .ornament {
    background-image: url("data:image/svg+xml,%3Csvg width='40' height='20' xmlns='http://www.w3.org/2000/svg'%3E%3Cpath d='M20 10 L10 5 L15 10 L10 15 L20 10 L30 15 L25 10 L30 5 Z' fill='%23E4A5B8' opacity='0.7'/%3E%3C/svg%3E");
    background-repeat: no-repeat;
    background-position: center;
  }
}

@layer utilities {
  .animate-fade-in {
    animation: fadeIn 1s ease-in-out;
  }

  .animate-slide-up {
    animation: slideUp 0.8s ease-out;
  }

  .animate-slide-in-left {
    animation: slideInLeft 0.8s ease-out;
  }

  .animate-slide-in-right {
    animation: slideInRight 0.8s ease-out;
  }

  .animate-fade-in-scale {
    animation: fadeInScale 0.8s ease-out;
  }

  .animate-bounce-gentle {
    animation: bounceGentle 2s infinite;
  }

  .animate-heartbeat {
    animation: heartbeat 1.5s ease-in-out infinite;
  }

  .animate-float {
    animation: float 3s ease-in-out infinite;
  }

  /* Animation states */
  .opacity-0 {
    opacity: 0;
  }

  .animate-on-scroll {
    opacity: 0;
    transform: translateY(30px);
    transition: all 0.8s cubic-bezier(0.25, 0.46, 0.45, 0.94);
  }

  .animate-on-scroll.animate-slide-up {
    opacity: 1;
    transform: translateY(0);
  }

  .animate-on-scroll.animate-slide-in-left {
    opacity: 1;
    transform: translateX(0);
  }

  .animate-on-scroll.animate-slide-in-right {
    opacity: 1;
    transform: translateX(0);
  }

  .animate-on-scroll.animate-fade-in-scale {
    opacity: 1;
    transform: scale(1);
  }

  .animate-on-scroll.animate-slide-in-left {
    transform: translateX(-50px);
  }

  .animate-on-scroll.animate-slide-in-right {
    transform: translateX(50px);
  }
}

@keyframes fadeIn {
  from { 
    opacity: 0; 
    transform: translateY(20px); 
  }
  to { 
    opacity: 1; 
    transform: translateY(0); 
  }
}

@keyframes slideUp {
  from { 
    opacity: 0; 
    transform: translateY(30px); 
  }
  to { 
    opacity: 1; 
    transform: translateY(0); 
  }
}

@keyframes bounceGentle {
  0%, 20%, 50%, 80%, 100% { 
    transform: translateY(0); 
  }
  40% { 
    transform: translateY(-10px); 
  }
  60% { 
    transform: translateY(-5px); 
  }
}

@keyframes heartbeat {
  0%, 100% { 
    transform: scale(1); 
  }
  25% { 
    transform: scale(1.15); 
  }
  50% { 
    transform: scale(1.05); 
  }
  75% { 
    transform: scale(1.2); 
  }
}

@keyframes slideInLeft {
  from { 
    opacity: 0; 
    transform: translateX(-50px); 
  }
  to { 
    opacity: 1; 
    transform: translateX(0); 
  }
}

@keyframes slideInRight {
  from { 
    opacity: 0; 
    transform: translateX(50px); 
  }
  to { 
    opacity: 1; 
    transform: translateX(0); 
  }
}

@keyframes fadeInScale {
  from { 
    opacity: 0; 
    transform: scale(0.9); 
  }
  to { 
    opacity: 1; 
    transform: scale(1); 
  }
}

@keyframes float {
  0%, 100% { 
    transform: translateY(0px); 
  }
  50% { 
    transform: translateY(-10px); 
  }
}

/* Responsive Hero Background Positioning */
.hero-bg-responsive {
  background-position: center 20% !important;
}

@media (max-width: 768px) {
  .hero-bg-responsive {
    background-position: 55% center !important;
    background-size: cover !important;
  }
}

