import { useState } from "react";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { useMutation } from "@tanstack/react-query";
import { insertRsvpSchema, type InsertRsvp } from "@shared/schema";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { RadioGroup, RadioGroupItem } from "@/components/ui/radio-group";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { Label } from "@/components/ui/label";
import { weddingConfig } from "@/config/wedding-config";
import { useScrollAnimation } from "@/hooks/use-scroll-animation";

export default function RsvpSection() {
  const { toast } = useToast();
  const titleRef = useScrollAnimation('animate-fade-in-scale');
  const formRef = useScrollAnimation('animate-slide-up');

  const form = useForm<InsertRsvp>({
    resolver: zodResolver(insertRsvpSchema),
    defaultValues: {
      firstName: "",
      lastName: "",
      email: "",
      guestCount: "",
      guestNames: "",
      attendance: "attending"
    }
  });

  const rsvpMutation = useMutation({
    mutationFn: async (data: InsertRsvp) => {
      const response = await apiRequest("POST", "/api/rsvp", data);
      return response.json();
    },
    onSuccess: (data) => {
      toast({
        title: "’á’∂’∏÷Ä’∞’°’Ø’°’¨’∏÷Ç’©’µ’∏÷Ç’∂!",
        description: data.message,
      });
      form.reset();
    },
    onError: (error: any) => {
      toast({
        title: "’ç’≠’°’¨",
        description: error.message || "’è’•’≤’´ ’ß ’∏÷Ç’∂’•÷Å’•’¨ ’Ω’≠’°’¨",
        variant: "destructive",
      });
    }
  });

  const onSubmit = (data: InsertRsvp) => {
    rsvpMutation.mutate(data);
  };

  return (
    <section id="rsvp" className="py-12 sm:py-20 bg-gradient-to-r from-lightGold/20 to-warmBeige/30">
      <div className="max-w-2xl mx-auto px-4 sm:px-6 lg:px-8">
        <div ref={titleRef} className="text-center mb-12 sm:mb-16 animate-on-scroll">
          <h2 className="text-3xl sm:text-4xl md:text-5xl lg:text-6xl text-charcoal mb-6 sm:mb-8 leading-tight" 
              style={{ 
                fontFamily: 'Playfair Display, serif', 
                fontWeight: '300'
              }}
              data-testid="text-rsvp-title">
            {weddingConfig.rsvp.title}
          </h2>
          <div className="w-16 sm:w-24 h-0.5 bg-softGold mx-auto mb-6 sm:mb-8"></div>
          <p className="text-charcoal/70 text-base sm:text-lg px-4" data-testid="text-rsvp-description">
            {weddingConfig.rsvp.description}
          </p>
        </div>

        <div ref={formRef} className="bg-white rounded-xl shadow-xl p-4 sm:p-6 lg:p-8 animate-on-scroll" data-testid="rsvp-form-container">
          <Form {...form}>
            <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-4 sm:space-y-6" data-testid="rsvp-form">
              <div className="grid grid-cols-1 sm:grid-cols-2 gap-4">
                <FormField
                  control={form.control}
                  name="firstName"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>{weddingConfig.rsvp.form.firstName}</FormLabel>
                      <FormControl>
                        <Input 
                          placeholder={weddingConfig.rsvp.form.firstNamePlaceholder} 
                          {...field} 
                          data-testid="input-first-name"
                        />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />
                <FormField
                  control={form.control}
                  name="lastName"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>{weddingConfig.rsvp.form.lastName}</FormLabel>
                      <FormControl>
                        <Input 
                          placeholder={weddingConfig.rsvp.form.lastNamePlaceholder} 
                          {...field} 
                          data-testid="input-last-name"
                        />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />
              </div>

              <FormField
                control={form.control}
                name="email"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>{weddingConfig.rsvp.form.email}</FormLabel>
                    <FormControl>
                      <Input 
                        type="email"
                        placeholder={weddingConfig.rsvp.form.emailPlaceholder} 
                        {...field} 
                        data-testid="input-email"
                      />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <FormField
                control={form.control}
                name="guestCount"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>{weddingConfig.rsvp.form.guestCount}</FormLabel>
                    <Select onValueChange={field.onChange} value={field.value}>
                      <FormControl>
                        <SelectTrigger data-testid="select-guest-count">
                          <SelectValue placeholder={weddingConfig.rsvp.form.guestCountPlaceholder} />
                        </SelectTrigger>
                      </FormControl>
                      <SelectContent>
                        {weddingConfig.rsvp.guestOptions.map((option) => (
                          <SelectItem key={option.value} value={option.value}>
                            {option.label}
                          </SelectItem>
                        ))}
                      </SelectContent>
                    </Select>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <FormField
                control={form.control}
                name="guestNames"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>{weddingConfig.rsvp.form.guestNames}</FormLabel>
                    <FormControl>
                      <Textarea 
                        placeholder={weddingConfig.rsvp.form.guestNamesPlaceholder}
                        rows={3}
                        {...field} 
                        data-testid="textarea-guest-names"
                      />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <FormField
                control={form.control}
                name="attendance"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>{weddingConfig.rsvp.form.attendance}</FormLabel>
                    <FormControl>
                      <RadioGroup
                        onValueChange={field.onChange}
                        value={field.value}
                        className="space-y-3"
                        data-testid="radio-attendance"
                      >
                        <div className="flex items-center space-x-3">
                          <RadioGroupItem value="attending" id="attending" />
                          <Label htmlFor="attending">{weddingConfig.rsvp.form.attendingYes}</Label>
                        </div>
                        <div className="flex items-center space-x-3">
                          <RadioGroupItem value="not-attending" id="not-attending" />
                          <Label htmlFor="not-attending">{weddingConfig.rsvp.form.attendingNo}</Label>
                        </div>
                      </RadioGroup>
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <Button 
                type="submit" 
                className="w-full bg-softGold hover:bg-softGold/90 text-white py-3 sm:py-4 font-medium transition-colors duration-300 transform hover:scale-105 text-sm sm:text-base"
                disabled={rsvpMutation.isPending}
                data-testid="button-submit-rsvp"
              >
                {rsvpMutation.isPending ? weddingConfig.rsvp.form.submittingButton : weddingConfig.rsvp.form.submitButton}
              </Button>
            </form>
          </Form>
        </div>
      </div>
    </section>
  );
}

client/src/components/timeline-section.t.content:
import { Clock, MapPin, Users, Music } from "lucide-react";
import { weddingConfig } from "@/config/wedding-config";
import {
  useScrollAnimation,
  useStaggeredAnimation,
} from "@/hooks/use-scroll-animation";

export default function TimelineSection() {
  const eventIcons = [
    <MapPin className="w-8 h-8" />,
    <Users className="w-8 h-8" />,
    <Music className="w-8 h-8" />,
    <Clock className="w-8 h-8" />,
  ];

  const titleRef = useScrollAnimation("animate-slide-up");
  const cardsRef = useStaggeredAnimation(200);

  return (
    <section
      id="timeline"
      className="py-24 bg-gradient-to-br from-softGold/10 via-lightGold/15 to-warmBeige/20 relative overflow-hidden"
    >
      <div className="max-w-6xl mx-auto px-4">
        {/* Title */}
        <div ref={titleRef} className="text-center mb-20 animate-on-scroll">
          <h2
            className="text-5xl md:text-6xl text-charcoal mb-8"
            style={{
              fontFamily: "Playfair Display, serif",
              fontWeight: "300",
            }}
            data-testid="text-timeline-title"
          >
            {weddingConfig.timeline.title}
          </h2>
          <div className="w-24 h-0.5 bg-softGold mx-auto mb-8"></div>
        </div>

        {/* Timeline Cards */}
        <div
          ref={cardsRef}
          className="grid md:grid-cols-2 lg:grid-cols-3 gap-8"
        >
          {weddingConfig.timeline.events.map((event, index) => (
            <div
              key={index}
              className="group relative animate-on-scroll"
              data-testid={`timeline-event-${index}`}
            >
              {/* Card */}
              <div className="bg-white/90 backdrop-blur-sm rounded-2xl p-8 shadow-xl border border-softGold/20 hover:shadow-2xl hover:scale-105 transition-all duration-500 relative overflow-hidden">
                {/* Background Pattern */}
                <div className="absolute top-0 right-0 w-20 h-20 bg-gradient-to-br from-softGold/10 to-lightGold/20 rounded-bl-full"></div>

                {/* Icon */}
                <div className="flex justify-center mb-6">
                  <div className="w-16 h-16 bg-gradient-to-br from-softGold to-lightGold rounded-full flex items-center justify-center text-white shadow-lg group-hover:scale-110 transition-transform duration-300">
                    {eventIcons[index]}
                  </div>
                </div>

                {/* Content */}
                <div className="text-center space-y-4">
                  <div
                    className="text-3xl md:text-4xl font-light text-charcoal"
                    style={{ fontFamily: "Playfair Display, serif" }}
                    data-testid={`timeline-time-${index}`}
                  >
                    {event.time}
                  </div>

                  <div
                    className="text-xl font-semibold text-charcoal"
                    data-testid={`timeline-title-${index}`}
                  >
                    {event.title}
                  </div>

                  {event.description && (
                    <div
                      className="text-charcoal/70 text-sm leading-relaxed"
                      data-testid={`timeline-description-${index}`}
                    >
                      {event.description}
                    </div>
                  )}
                </div>

                {/* Step Number */}
              </div>

              {/* Connector Line (except last item) */}
              {index < weddingConfig.timeline.events.length - 1 && (
                <div className="hidden lg:block absolute top-1/2 -right-4 w-8 h-0.5 bg-gradient-to-r from-softGold/50 to-lightGold/30 z-10"></div>
              )}
            </div>
          ))}
        </div>

        {/* Thank You Message After Timeline */}
        {weddingConfig.timeline.afterMessage && (
          <div className="mt-20 text-center">
            <div
              className="text-2xl md:text-3xl text-charcoal mb-8"
              style={{
                fontFamily: "Playfair Display, serif",
                fontWeight: "300",
              }}
              data-testid="timeline-thank-you"
            >
              {weddingConfig.timeline.afterMessage.thankYou}
            </div>

            <div className="w-24 h-0.5 bg-softGold mx-auto mb-8"></div>

            <div
              className="text-charcoal/80 text-lg max-w-3xl mx-auto leading-relaxed bg-white/60 backdrop-blur-sm rounded-2xl p-8 shadow-lg border border-softGold/20"
              style={{ whiteSpace: "pre-line" }}
              data-testid="timeline-notes"
            >
              {weddingConfig.timeline.afterMessage.notes}
            </div>
          </div>
        )}
      </div>
    </section>
  );
}

