import { useState, useEffect } from "react";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Switch } from "@/components/ui/switch";
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from "@/components/ui/table";
import { Dialog, DialogContent, DialogDescription, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { useToast } from "@/hooks/use-toast";
import { Settings, Shield, Users, Download, Mail, Calendar, CheckCircle, XCircle, Eye, QrCode, Printer, Link2 } from "lucide-react";

interface Rsvp {
  id: string;
  firstName: string;
  lastName: string;
  email: string;
  guestCount: string;
  guestNames?: string | null;
  attendance: string;
  createdAt: string;
}

export function AdminPanel() {
  const [password, setPassword] = useState("");
  const [authenticated, setAuthenticated] = useState(false);
  const [maintenanceEnabled, setMaintenanceEnabled] = useState(false);
  const [loading, setLoading] = useState(false);
  const [rsvps, setRsvps] = useState<Rsvp[]>([]);
  const [rsvpCount, setRsvpCount] = useState(0);
  const [attendingCount, setAttendingCount] = useState(0);
  const [notAttendingCount, setNotAttendingCount] = useState(0);
  const [selectedRsvp, setSelectedRsvp] = useState<Rsvp | null>(null);
  const { toast } = useToast();

  useEffect(() => {
    if (authenticated) {
      loadData();
    }
  }, [authenticated]);

  const loadData = async () => {
    try {
      // Load maintenance status first
      const maintenanceResponse = await fetch("/api/maintenance");
      const maintenanceData = await maintenanceResponse.json();
      console.log("üîß Maintenance status loaded:", maintenanceData);
      setMaintenanceEnabled(maintenanceData.enabled);

      // Load RSVP data
      const rsvpResponse = await fetch("/api/rsvps");
      const rsvpData = await rsvpResponse.json();

      setRsvps(rsvpData);
      setRsvpCount(rsvpData.length);

      // Calculate attendance statistics
      const attending = rsvpData.filter((rsvp: Rsvp) => rsvp.attendance === "attending").length;
      const notAttending = rsvpData.filter((rsvp: Rsvp) => rsvp.attendance === "not-attending").length;

      setAttendingCount(attending);
      setNotAttendingCount(notAttending);
    } catch (error) {
      console.error("Failed to load admin data:", error);
    }
  };

  const handleAuthenticate = async (e: React.FormEvent) => {
