      "First Name",
      "Last Name", 
      "Email",
      "Guest Count",
      "Guest Names",
      "Attendance",
      "Submitted Date"
    ];

    const csvContent = [
      headers.join(","),
      ...rsvps.map(rsvp => [
        `"${rsvp.firstName}"`,
        `"${rsvp.lastName}"`,
        `"${rsvp.email}"`,
        `"${rsvp.guestCount}"`,
        `"${rsvp.guestNames || ''}"`,
        `"${rsvp.attendance === 'attending' ? 'Attending' : 'Not Attending'}"`,
        `"${new Date(rsvp.createdAt).toLocaleDateString()}"`
      ].join(","))
    ].join("\n");

    const blob = new Blob([csvContent], { type: "text/csv" });
