import path from 'path';
import { fileURLToPath } from 'url';

const __filename = fileURLToPath(import.meta.url);
const __dirname = path.dirname(__filename);

async function generateQRCode() {
  const url = 'https://harutandtatev.com/photos';
  const outputPath = path.join(__dirname, '../client/public/wedding-photos-qr.png');

  try {
    // Generate QR code with custom options
    await QRCode.toFile(outputPath, url, {
      width: 300,
      margin: 2,
      color: {
        dark: '#2c3e50',  // Dark color for QR code
        light: '#F8F6F1'  // Cream background to match wedding theme
      }
    });

    console.log('‚úÖ QR Code generated successfully!');
    console.log(`üì± QR Code saved to: ${outputPath}`);
    console.log(`üîó URL: ${url}`);
  } catch (err) {
    console.error('‚ùå Error generating QR code:', err);
  }
}

generateQRCode();
