# Test API endpoints directly\n$baseUrl = \"http://localhost:5001\"\n\nWrite-Host \"Testing Wedding Platform API Endpoints\"\nWrite-Host \"========================================\"\n\n# Test health endpoint\ntry {\n    Write-Host \"Testing /health endpoint...\"\n    $response = Invoke-RestMethod -Uri \"$baseUrl/health\" -Method GET\n    Write-Host \"✅ Health endpoint working: $($response.status)\"\n} catch {\n    Write-Host \"❌ Health endpoint failed: $_\"\n}\n\n# Test API test endpoint\ntry {\n    Write-Host \"Testing /api/test endpoint...\"\n    $response = Invoke-RestMethod -Uri \"$baseUrl/api/test\" -Method GET\n    Write-Host \"✅ API test endpoint working: $($response.message)\"\n} catch {\n    Write-Host \"❌ API test endpoint failed: $_\"\n}\n\n# Test template config endpoint\ntry {\n    Write-Host \"Testing template config endpoint...\"\n    $response = Invoke-RestMethod -Uri \"$baseUrl/api/templates/default-harut-tatev/config\" -Method GET\n    Write-Host \"✅ Template config endpoint working\"\n    if ($response.config.couple) {\n        Write-Host \"✅ Template has couple info: $($response.config.couple.groomName) & $($response.config.couple.brideName)\"\n    }\n} catch {\n    Write-Host \"❌ Template config endpoint failed: $_\"\n}\n\n# Test templates list\ntry {\n    Write-Host \"Testing templates list endpoint...\"\n    $response = Invoke-RestMethod -Uri \"$baseUrl/api/templates\" -Method GET\n    Write-Host \"✅ Templates list endpoint working: $($response.Count) templates found\"\n} catch {\n    Write-Host \"❌ Templates list endpoint failed: $_\"\n}\n\n# Test by slug\ntry {\n    Write-Host \"Testing template by slug...\"\n    $response = Invoke-RestMethod -Uri \"$baseUrl/api/templates/harut-tatev/config\" -Method GET\n    Write-Host \"✅ Template by slug working\"\n} catch {\n    Write-Host \"❌ Template by slug failed: $_\"\n}\n\nWrite-Host \"\\nAPI testing completed.\"